mapper.select:
SELECT t1.workorderid ,t1.customerid ,t1.customername ,t1.centificatenumber ,t1.aging ,t2.name AS agingtime ,t1.problemtype ,t3.name as problemtypename ,t1.handlegroup ,t4.name AS handlegroupname ,t1.handleperson ,t5.USERNAME ,t1.urgencylevel ,t6.name AS urgencylevelname ,t1.status ,t1.creatdate ,t1.hurrycount ,t1.firstdate FROM t_workorder AS t1 LEFT JOIN t_data t2 ON t1.aging=t2.id LEFT JOIN t_data t3 ON t1.problemtype=t3.id LEFT JOIN t_dept t4 ON t1.handlegroup=t4.id LEFT JOIN t_user t5 ON t1.handleperson=t5.USERID LEFT JOIN t_data t6 ON t1.urgencylevel=t6.id
AND t1.status=#{wo.status}
AND t1.handleperson=#{wo.handleperson}
AND t1.handlegroup=#{wo.handlegroup}
AND t1.workorderid=#{wo.workorderid}
AND date_format(t1.creatdate,'%Y-%m-%d')=#{creatdateStr}
AND t1.customername =#{wo.customername}
AND t1.centificatenumber=#{wo.centificatenumber}
AND t1.urgencylevel=#{wo.urgencylevel}
AND t1.problemtype=#{wo.problemtype}
